import React, {Component} from 'react';

class ComponentLifeCycle extends Component {

    // има 3 цикъла mounting(когато компонента се създава), updating(когато се ъпдейтва компонента)
        // , unmounting (когато компонента се дестройва)

    constructor(props){

        super(props);

        this.state = {counter:0};
    }

    componentDidMount(){
        //тук слагаме неща които искаме да се извършат което компонента се създава извиква се след конструктора
        setinterval(this.increment,1000)
    }

    increment(){

        this.setState((prevState) =>{
            return {counter: ++ prevState.counter}
        });

    }

    render(){
        return (

            <div style={{color:'green'}}>

                <h1>{this.state.counter}</h1>

            </div>

        )
    }

}

export default ComponentLifeCycle
