//use state in function component

function App(){

  const [counter, setCounter] = setState(0);//here we use destructoring because this setState function
  //returns two things: first is function which change state which initial value we put as parameter
  //in our case is 0; second is variable which is pointer to value of state and through it we can access it
  return (
    <div className='App'>
      <h1></h1>
      <br/>
      <button>Increse</button>
      <br/>
      <br/>
      <button >Reset</button>
    </div>
  )

}

