  )

}

function useRandomNumberOnInterval(){

  const initial = 0;
  const [random, setRandom] = useState(initial);

  useEffect(()=>{ 
    let randomNuber = setInterval(
      ()=>{

        setRandom((Math.random()*100).toFixed(0))
      }
      ,1000);

    return () => clearInterval(randomNuber);

  })

