import React,{Component} from 'react'

class Lottery extends Component{


  renderButton (){

    if(this.props.remainingTickets > 0){
      return <button onclick={this.props.actions.remainingTickets}>Купи билет</button>//here we return button in which
      //we have defined prop actions which is object we have defined where we use props. actions in App component 
    }

    return <h1>Билетите свършиха</h1>
    
  }

  render(){

    return (

        <>
          <h2>Лотария</h2>
          {this.renderButton()}
          <br/>
          <small>Оставащи билети: {this.props.remainingTickets}</small>
        </>
   
      );

  }
  
}

export default Lottery;
