    super(props);
    this.state = {
      winningNumber    : getRandomNumber(),
      remainingTickets : [],
      tickets          :5,
      finished         : false
    }
  }

  renderApp(){  //в тази ф-я на база на условна конструкция ще казваме дали даден компонент ще се зарежда или не

  }

  render(){
    return (

      <div className ='App'>
        <Lottery remainingTickets = {this.state.remainingTickets}/>

      </div>

      );

  }

}

export default App;
