import React, { Component} from 'react';

class FormComponent extends Component {
constructor(props){
    super(props);
    this.state = {

        name: '',
        email:'',
        gender:''

    }
}

render(){
    return(
        <>
            <form>
                <input type="text" id="name" placeholder="име"/>
                <br/>
                <input type="text" id="email" placeholder="мейл"/>
                <br/>
                <select  id="gender" placeholder="пол">
                    <option />
                    <option value="мъж">Мъж</option>
                    <option value="мъж">Жена</option>
                </select>
                <br/>
                <div>
                    <p>Име: {this.state.name}</p>
                    <p>Име: {this.state.email}</p>
                    <p>Име: {this.state.gender}</p>
                </div>
                <br/>
                <button type="submit">Изпрати</button>

            </form>
        </>
    )
}

}

export default FormComponent
